var supported_extensions = [
    'blackfire',
]
var unsupported_extensions = []
_.forEach(
service.runtime.extensions,
function (extension) {
  if (typeof(extension) == "object") {
    if (!("name" in extension)) {
      throw("Invalid format for extension: " + JSON.stringify(extension));
    }
    extension = extension["name"];
  }
  if (supported_extensions.indexOf(extension) == -1) {
    unsupported_extensions.push(extension);
  }
}
);
if (unsupported_extensions.length == 1) {
throw("The \"" + unsupported_extensions[0] + "\" extension is not supported for " + service.type);
}
if (unsupported_extensions.length > 1) {
throw("The following extensions are not supported for " + service.type + ": "+ unsupported_extensions.join(", "));
}
