scripts.deployment:
function getRabbitMQVersions(deployment) {
    const versions = [];
    for (const svc of Object.values(deployment?.services || {})) {
        if (typeof svc.type === 'string' && svc.type.startsWith('rabbitmq:')) {
            const match = svc.type.match(/^(rabbitmq:\d+\.\d+)/);
            if (match) {
                versions.push(match[1]);
            }
        }
    }
    return versions;
}

function isRabbitMQUpgradeAllowed(newDeployment, oldDeployment) {
    if (!oldDeployment) return { allowed: true };

    const oldVersions = getRabbitMQVersions(oldDeployment);
    const newVersions = getRabbitMQVersions(newDeployment);

    const allowedPairs = [
        ["rabbitmq:3.8", "rabbitmq:3.9"],
        ["rabbitmq:3.9", "rabbitmq:3.10"],
        ["rabbitmq:3.10", "rabbitmq:3.11"],
        ["rabbitmq:3.11", "rabbitmq:3.12"],
        ["rabbitmq:3.12", "rabbitmq:3.13"],
        ["rabbitmq:3.13", "rabbitmq:4.0"],
        ["rabbitmq:3.13", "rabbitmq:4.1"]
    ];

    const invalidPairs = [];

    for (const key of Object.keys(oldVersions)) {
        const oldVersion = oldVersions[key];
        const newVersion = newVersions[key];

        if (!newVersion || oldVersion === newVersion) continue;

        const isAllowed = allowedPairs.some(
            ([from, to]) => from === oldVersion && to === newVersion
        );

        if (!isAllowed) {
            invalidPairs.push(`Upgrade from ${oldVersion} to ${newVersion} is not allowed.`);
        }
    }

    if (invalidPairs.length > 0) {
        return {
            allowed: false,
            message: invalidPairs.join("\n") + "\n\nPlease refer to https://docs.upsun.com/add-services/rabbitmq.html#upgrading"
        };
    }

    return { allowed: true };
}

const result = isRabbitMQUpgradeAllowed(new_deployment, old_deployment);

if (!result.allowed) {
    invalid.project_info_runtime = result.message;
}

