    for (const [name, endpoint] of Object.entries(service.configuration.endpoints)) {
        if (endpoint.hasOwnProperty("default") && endpoint["default"] == true) {
            default_endpoint_name = name;  // but if we encounter an explicit default, then that one
        }
    }
    _.forEach(
        service.configuration.endpoints,
